			toggleAngleControl(selectedMode) {
				if ([1, 3, 5, 6, 7].includes(selectedMode)) {
					this.showHeightControl = true;
				} else {
					this.showHeightControl = false;
				}
			},

			// 控制发球高度选择功能的显示与隐藏
			toggleHeightSelector(selectedMode) {
